/*=========================================================================================
  File Name: moduleE processMutations.js
  Description: E process Module Mutations
  ----------------------------------------------------------------------------------------
  Item Name: Vuesax Admin - VueJS Dashboard Admin Template
  Author: Pixinvent
  Author URL: http://www.themeforest.net/user/pixinvent

  DIVISOFT   MUTACIONES PARA LA SECCION DE CORREOS
==========================================================================================*/



export default {
    SET_DBPM_SEARCH_QUERY(state, query) {
        state.processSearchQuery = query;
    },
    UPDATE_DBPM_FILTER(state, filterName) {
        state.process_filter = filterName
    },
    TOGGLE_IS_DBPM_STARRED(state, payload) {
        state.mails.forEach((mail) => {
            if (mail.id == payload.mailId) {
                mail.isStarred = payload.value
            }
        })
    },
    MOVE_MAILS_TO(state, payload) {
        payload.mails.forEach((mailId) => {
            const mailIndex = state.bpmprocess.findIndex((mail) => mail.id == mailId);

            state.mails[mailIndex].mailType = payload.to;
        })
    },
    UPDATE_DBPM_UNREAD(state, payload) {
        payload.mails.forEach((mailId) => {
            const mailIndex = state.bpmprocess.findIndex((mail) => mail.id == mailId);

            state.bpmprocess[mailIndex].unread = payload.unread;
        })
    },
    ADD_LABEL_TO_MAILS(state, payload) {
        payload.mails.forEach((mailId) => {
            const mailIndex = state.bpmprocess.findIndex((mail) => mail.id == mailId);

            const index = state.bpmprocess[mailIndex].labels.indexOf(payload.label)

            if (index == -1) {
                state.bpmprocess[mailIndex].labels.push(payload.label)
            } else {
                state.bpmprocess[mailIndex].labels.splice(index, 1);
            }
        })
    },
    UPDATE_DBPM_LABELS(state, payload) {
        const mailIndex = state.bpmprocess.findIndex((mail) => mail.id == payload.mailId);
        state.bpmprocess[mailIndex].labels = payload.value;
    },
}
