/*=========================================================================================
  File Name: actions.js
  Description: Vuex Store - actions
  ----------------------------------------------------------------------------------------
  Item Name: Vuexy - Vuejs, HTML & Laravel Admin Dashboard Template
  Author: Pixinvent
  Author URL: http://www.themeforest.net/user/pixinvent
==========================================================================================*/
import axios from 'axios'
import router from '@/router'
import * as divilib from "@/divisoftlibs/utilDivisoftJS.js";

const actions = {

    // /////////////////////////////////////////////
    // COMPONENTS
    // /////////////////////////////////////////////

    // Vertical NavMenu
    updateVerticalNavMenuWidth({ commit }, width) {
        commit('UPDATE_VERTICAL_NAV_MENU_WIDTH', width)
    },

    // VxAutoSuggest
    updateStarredPage({ commit }, payload) {
        commit('UPDATE_STARRED_PAGE', payload)
    },

    // The Navbar
    arrangeStarredPagesLimited({ commit }, list) {
        commit('ARRANGE_STARRED_PAGES_LIMITED', list)
    },
    arrangeStarredPagesMore({ commit }, list) {
        commit('ARRANGE_STARRED_PAGES_MORE', list)
    },

    // /////////////////////////////////////////////
    // UI
    // /////////////////////////////////////////////

    toggleContentOverlay({ commit }) {
        commit('TOGGLE_CONTENT_OVERLAY')
    },
    updateTheme({ commit }, val) {
        commit('UPDATE_THEME', val)
    },

    // /////////////////////////////////////////////
    // User/Account
    // /////////////////////////////////////////////

    updateUserInfo({ commit }, payload) {
        commit('UPDATE_USER_INFO', payload)
    },

    updateUserRole({ dispatch }, payload) {
        // Change client side
        payload.aclChangeRole(payload.userRole)

        // Make API call to server for changing role

        // Change userInfo in localStorage and store
        dispatch('updateUserInfo', { userRole: payload.userRole })
    },

    // ////////////////////////////////////////////
    // DIVISOFT
    // ////////////////////////////////////////////



    /// llama a Solicitud de Datos 
    /// llama a Solicitud de Datos 
    async acDsoa({ commit, state }, pet) {
        const datos = state.peticion;
        const url = state.urlDsoa;
        // console.log("peticion ", JSON.stringify(pet))

        //limpia mensaje error
        var jsonError = { codigoError: '', msgError: '' };
        commit('mutRegistraError', jsonError)

        if (pet.Credencial == null || pet.ObjectId == null) {
            alert('adentro')

            if (pet.Credencial == null)
                var jsonError = { codigoError: '90', msgError: 'Credencial Nulo' };

            if (pet.ObjectId == null)
                var jsonError = { codigoError: '91', msgError: 'Objeto BD no identificado' };

            commit('mutRegistraError', jsonError)
        } else {
            var dsoaModel = {
                datos: datos,
                manipula: "",
                bd: "",
                dml: pet.dml,
                formato: pet.formatoenvio,
                formatoRequest: pet.formatorecibe,
                credencial: pet.credencial,
                tecnologia: "",
                datosJson: "",
                objectId: pet.ObjectId,
            };

            //            console.log('DSOAMODEL2: ' + JSON.stringify(dsoaModel));

            console.log("url: " + url);

            commit('mutProcesandotrue')

            try {
                // fetch data from a url web service

                let res = await axios({
                    method: 'POST',
                    url,
                    headers: {
                        "content-type": "application/json",
                    },
                    "processData": false,
                    "contentType": false,
                    "mimeType": "multipart/form-data",
                    "sync": false,
                    "crossDomain": true,
                    "data": dsoaModel
                })

                .then((response) => {

                        commit('mutProcesandoFalse');

                        console.log("LO QUE RESPONDIO CMS", JSON.stringify(response.data));


                        console.log("INDICADOR", pet.indicador);


                        //evalua el indicador
                        if (pet.indicador == 1) // llena un grid  
                        {

                            if (response.data.codigoerror == "2000") // No AUTORIZADO  Enviar  login
                            //router
                                router.push('/pages/login').catch(() => {})
                            else
                                commit('mutSetRespuestaGrid', response.data); //carga variable con los datos
                        }

                        if (pet.indicador == 2) //llena un registro unico para edicion
                            commit('mutSetRespuestaForm', response.data);


                        if (pet.indicador == 3) // respuesta de Base datos para INSERT,DELETE,UPDATE
                            commit('mutSetRespuestaCrud', response.data);


                        if (pet.indicador == 4) // respuesta de Base datos para EXECUTE
                            commit('mutSetRespuestaCrud', response.data);


                        console.log("procesando indicador ", pet.indicador);


                        if (pet.indicador == 5) // respuesta de Base datos para EXECUTE
                            commit('mutSetRespuestaPrompt', response.data);


                        if (pet.indicador == 7) // respuesta procedimiento Almacenado
                            commit('mutSetRespuestaE', response.data);

                    })
                    .catch(function(error) {
                        commit('mutProcesandoFalse');
                        if (error.response) {
                            // Request made and server responded
                            console.log(error.response.data);
                            console.log(error.response.status);
                            console.log(error.response.headers);
                        } else if (error.request) {
                            // The request was made but no response was received
                            console.log(error.request);
                        } else {
                            // Something happened in setting up the request that triggered an Error
                            console.log("SE PRODUJO ERROR " + error);
                        }
                    });

            } catch (error) {
                commit('mutProcesandoFalse');
                alert(error); // catches both errors
            }
        }

    },


    ///  MULTIMEDIA MULTIMEDIA  MULTIMEDIA MULTIMEDIA  MULTIMEDIA MULTIMEDIA  MULTIMEDIA MULTIMEDIA  MULTIMEDIA MULTIMEDIA 
    async CMSacDsoa({ commit, state }, pet) {

        if (pet.Credencial == null) {

            if (pet.Credencial == null)
                var jsonError = { codigoError: '90', msgError: 'Credencial Nulo CMSacDsoa' };


            commit('mutRegistraError', jsonError)
        } else {
            commit('mutsetDatosDefaultCMS', divilib.DataCMS[0]);

            const url = state.urlDsoaCMS;
            commit('mutProcesandotrue');
            try {
                // fetch data from a url web service
                console.log("LO QUE  CMS ENVIA1   ", JSON.stringify(pet));
                let CMSres = await axios({
                    method: 'POST',
                    url,
                    headers: {
                        "content-type": "application/json",
                    },
                    "processData": false,
                    "contentType": false,
                    "mimeType": "multipart/form-data",
                    "sync": false,
                    "crossDomain": true,
                    "data": pet
                })

                .then((response) => {
                        commit('mutProcesandoFalse');

                      console.log("LO QUE  CMS   RESPONDE   ", response);
                        let data = response.data.datos
                        if (data.length == 0 || data == null)
                            data = JSON.parse(response.data.datos)

                        commit('mutsetData', data);

                        data = response.data.bd
                        if (data.length == 0 || data == null)
                            data = JSON.parse(data)

                        commit('mutsetBd', data);

                        data = response.data.manipula;
                        if (data.length == 0 || data == null)
                            data = JSON.parse(data);

                        commit('mutsetManipulacion', data);


                    })
                    .catch(function(error) {
                        commit('mutProcesandoFalse');
                        if (error.response) {
                            // Request made and server responded
                            console.log(error.response.data);
                            console.log(error.response.status);
                            console.log(error.response.headers);
                        } else if (error.request) {
                            // The request was made but no response was received
                            console.log(error.request);
                        } else {
                            // Something happened in setting up the request that triggered an Error
                            console.log("SE PRODUJO ERROR " + error);
                        }
                    });

            } catch (error) {
                commit('mutProcesandoFalse');
                alert(error); // catches both errors
            }
        }
    },


    async CMSacDsoaLimpia({ commit, state }, pet) {
        // console.log("limpiando la Peticion ", pet)
        commit('mutsetDatosDefaultCMS', pet);
    }
}

export default actions
