import * as divilib from "@/divisoftlibs/utilDivisoftJS.js"; 

export default {

  data () {
    return {
      username: 'CMENESES',
      password: 'RrQK8n/T0OM=', //A en B64
      checkbox_remember_me: false,
      paramtxt: "",
      headertxt: ""
    }
  },

  computed: {
    validateForm () {
      return !this.errors.any() && this.email !== '' && this.password !== ''
    }
  },
  methods: {


    //función para iniciar sesión
    inicioSesion: function() {
      var origenUrl = "O";

      var header = {
                MODO: "E",
                OBJECTID: "97",
                CREDENCIAL: "REPLACECREDENCIAL" ,
                USERNAME: this.username,
                REGISTROSXPAGINA: "",
                PAGINA: "",
                ORDERBY: ""
            };

      //crea SdtLogin
	    var SdtLogin = {
          action :  "L" ,
          username :  this.username,
          password :  this.password,
          direccionIP :  "127.0.0.1",
          conexionADM :  "security",
          credencial: "REPLACECREDENCIAL"
      };

      var loginR = divilib.GetSdtLoginString(SdtLogin);

      //crea parametros
      var parametros = [];
      var parametro1 = { NOMBRE: "XML_SOLICITUD", OPERADOR:  "=" , VALOR1: loginR, VALOR2: "", CDATA: "1"};
      parametros.push(parametro1);

      //entrega arreglo de parametros recibe texto de parametros
      this.paramtxt = divilib.paramArraytoStrintg(parametros);
      this.headertxt = divilib.GetHeaderString(header);

      //Crea peticion
      var petTxt = {
        header: this.headertxt,
        parametros: this.paramtxt,
        filas: ""
      };

      this.$store.commit("dsoaLogin/mutsetURL", "O");
      this.$store.commit("dsoaLogin/mutSetPeticiontxt", petTxt);

    //agregar usuario a la hora
      var horaNumerico = Date.now(); 
      console.log("The time elapsed in millisecond is: " + horaNumerico); 

      //Crea pedido
      var pedido = {
          dml: "E", 
          ObjectId: "97",
          credencial: horaNumerico,
          formatoenvio: "N",
          formatorecibe: "N",
          indicador: "6", //LOGIN
          origenUrl: origenUrl  
      };

      //llama action
      this.$store.dispatch("dsoaLogin/acLoginDsoa", pedido);

    },

    checkLogin () {
      // If user is already logged in notify
      if (this.$store.state.auth.isUserLoggedIn()) {

        // Close animation if passed as payload
        // this.$vs.loading.close()

        this.$vs.notify({
          title: 'Login Attempt',
          text: 'You are already logged in!',
          iconPack: 'feather',
          icon: 'icon-alert-circle',
          color: 'warning'
        })

        return false
      }
      return true
    },

    login () {

      if (!this.checkLogin()) return

      // Loading
      this.$vs.loading()

      const payload = {
        checkbox_remember_me: this.checkbox_remember_me,
        userDetails: {
          email: this.email,
          password: this.password
        }
      }

      //action
      this.$store.dispatch('auth/loginJWT', payload)
        .then(() => { this.$vs.loading.close() })
        .catch(error => {
          this.$vs.loading.close()
          this.$vs.notify({
            title: 'Error',
            text: error.message,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          })
        })
    },

    registerUser () {
      //if (!this.checkLogin()) return
      this.$router.push('/pages/register').catch(() => {})
    },
  }
}
